import React from 'react';
import { Pokemon } from '../../types/pokemon';
import './PokemonCard.css';

interface PokemonCardProps {
  pokemon: Pokemon;
  isLeft: boolean;
}

const PokemonCard: React.FC<PokemonCardProps> = ({ pokemon, isLeft }) => {
  return (
    <div className={`pokemon-card ${isLeft ? 'left' : 'right'}`}>
      <img 
        src={isLeft ? pokemon.frontSprite : pokemon.backSprite} 
        alt={pokemon.name} 
        className="pokemon-sprite"
      />
      <h2>{pokemon.name}</h2>
      <p>Move: {pokemon.move.name}</p>
      <p>Power: {pokemon.move.power}</p>
    </div>
  );
};

export default PokemonCard;
