import { Pokemon, Move } from '../types/pokemon';

export const fetchPokemonData = async (): Promise<Pokemon> => {
  const id = Math.floor(Math.random() * 898) + 1;
  const response = await fetch(`https://pokeapi.co/api/v2/pokemon/${id}`);
  const data = await response.json();
  const moveData = await fetchMoveData(data.moves[Math.floor(Math.random() * data.moves.length)].move.name);

  return {
    name: data.name,
    frontSprite: data.sprites.front_default,
    backSprite: data.sprites.back_default,
    move: moveData
  };
};

export const fetchMoveData = async (moveName: string): Promise<Move> => {
  const response = await fetch(`https://pokeapi.co/api/v2/move/${moveName}`);
  const data = await response.json();
  return {
    name: data.name,
    power: data.power || 0
  };
};
